CREATE TABLE "public"."transfer" ("id" bytea NOT NULL, "to_address" bytea NOT NULL, "asset_id" bigint NOT NULL, "network_id" integer NOT NULL, "remote_asset_id" bytea NOT NULL, "amount" bigint NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("network_id") REFERENCES "public"."network"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("id"), CONSTRAINT "valid_id" CHECK (length(id) = 32), CONSTRAINT "valid_to_address" CHECK (length(to_address) = 20), CONSTRAINT "valid_remote_asset_id" CHECK (length(remote_asset_id) = 20));
